import React from 'react'

const Header = ( {pseudo} ) => {
    const formatPseudo = pseudo => /{aeiouy}/i.test(pseudo[0]) ? `d'${pseudo}` : `de ${pseudo}`
    return (
        <Header>
            <h1> La boîte à recette de {formatPseudo(pseudo)}</h1>

        </Header>
    )
}

export default Header
